mapper.select:
SELECT COUNT(A.SHOP_UID) AS CNT FROM T_SHOP A
,(SELECT B.SHOP_UID, C.ORDER_TIME FROM T_SHOP B, T_SHOP_TIME C WHERE B.SHOP_UID = C.SHOP_UID AND ORDER_TIME = '2200') D
WHERE 1 = 1
AND A.SHOP_TYPE = #{searchType}
AND A.SHOP_TYPE = #{searchType}
AND (SHOP_NAME LIKE '%' || #{searchValue} || '%' OR SHOP_LOCATION1 LIKE '%' || #{searchValue} || '%' OR SHOP_LOCATION2 LIKE '%' || #{searchValue} || '%' OR SHOP_LOCATION3 LIKE '%' || #{searchValue} || '%' OR SHOP_ADDRESS LIKE '%' || #{searchValue} || '%' OR SHOP_INTRO LIKE '%' || #{searchValue} || '%' OR SHOP_HASHTAG LIKE '%' || #{searchValue} || '%' )
AND A.SHOP_HOLIDAY != #{shopHoliday}
AND A.SHOP_UID = D.SHOP_UID

SELECT * FROM (SELECT ROWNUM AS RNUM, A.SHOP_UID AS SHOP_UID, NVL(A.USER_UID, '') AS USER_UID, NVL(A.SHOP_NAME, '') AS SHOP_NAME, NVL(A.SHOP_TYPE, '') AS SHOP_TYPE, NVL(A.SHOP_HASHTAG, '') AS SHOP_HASHTAG, NVL(A.SHOP_HOLIDAY, '') AS SHOP_HOLIDAY, NVL(A.SHOP_LOCATION1, '') AS SHOP_LOCATION1, NVL(A.SHOP_LOCATION2, '') AS SHOP_LOCATION2, NVL(A.SHOP_LOCATION3, '') AS SHOP_LOCATION3, NVL(A.SHOP_ADDRESS, '') AS SHOP_ADDRESS, NVL(A.SHOP_INTRO, '') AS SHOP_INTRO, NVL(B.FILE_SEQ, 0) AS FILE_SEQ, NVL(B.FILE_ORG_NAME, '') AS FILE_ORG_NAME, NVL(B.FILE_NAME, '') AS FILE_NAME, NVL(B.FILE_EXT, '') AS FILE_EXT, NVL(B.FILE_SIZE, 0) AS FILE_SIZE FROM T_SHOP A, T_SHOP_FILE B
,(SELECT C.SHOP_UID, C.ORDER_TIME FROM T_SHOP_TIME C WHERE ORDER_TIME = #{reservationTime}) D
WHERE A.SHOP_UID = B.SHOP_UID AND B.FILE_ORG_NAME LIKE '%'|| 'Shop_' ||'%'
AND SHOP_TYPE = '1'
AND SHOP_TYPE = '2'
AND (SHOP_NAME LIKE '%' || #{searchValue} || '%' OR SHOP_LOCATION1 LIKE '%' || #{searchValue} || '%' OR SHOP_LOCATION2 LIKE '%' || #{searchValue} || '%' OR SHOP_LOCATION3 LIKE '%' || #{searchValue} || '%' OR SHOP_ADDRESS LIKE '%' || #{searchValue} || '%' OR SHOP_INTRO LIKE '%' || #{searchValue} || '%' OR SHOP_HASHTAG LIKE '%' || #{searchValue} || '%' )
AND SHOP_HOLIDAY != #{shopHoliday}
AND A.SHOP_UID = D.SHOP_UID
) WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

		SELECT COUNT(ORDER_UID) AS CNT
  		  FROM T_ORDER
 		 WHERE USER_UID = #{userUID}

SELECT COUNT(*)
  FROM T_SHOP_BOOKMARK
 WHERE SHOP_UID = #{shopUID}
   AND USER_UID = #{userUID}

mapper.delete:
DELETE FROM T_SHOP_BOOKMARK
 WHERE SHOP_UID = #{shopUID}
   AND USER_UID = #{userUID}

